import { useState } from 'react';

export default function Dashboard({ jwt }) {
  const [tx, setTx] = useState([]);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    setLoading(true);
    const res = await fetch('/api/plaid/transactions', {
      headers: { Authorization: `Bearer ${jwt}` }
    });
    const data = await res.json();
    setTx(data.transactions || []);
    setLoading(false);
  };

  return (
    <div style={{ marginTop: 24 }}>
      <button onClick={load} disabled={loading}>
        {loading ? 'Loading…' : 'Load Transactions (Last 30 days)'}
      </button>

      <ul style={{ marginTop: 16, padding: 0, listStyle: 'none' }}>
        {tx.map(t => (
          <li key={t.transaction_id} style={{ padding: '8px 0', display: 'flex', justifyContent: 'space-between', borderBottom: '1px solid #eee' }}>
            <span>{t.date} — {t.name}</span>
            <span>${Math.abs(t.amount).toFixed(2)}</span>
          </li>
        ))}
        {tx.length === 0 && <li>No transactions loaded.</li>}
      </ul>
    </div>
  );
}
