import { useState } from 'react';
import LinkBankButton from './components/LinkBankButton';
import Dashboard from './components/Dashboard';

export default function App() {
  const [jwt, setJwt] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const register = async () => {
    const res = await fetch('/api/auth/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password })
    });
    const data = await res.json();
    alert(data.message || JSON.stringify(data));
  };

  const login = async () => {
    const res = await fetch('/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password })
    });
    const data = await res.json();
    if (data.token) setJwt(data.token);
    alert(data.message || (data.token ? 'Login OK' : 'Login failed'));
  };

  return (
    <div style={{ maxWidth: 720, margin: '2rem auto', padding: 16 }}>
      <h1>PFM Dashboard (MVP)</h1>

      {!jwt ? (
        <div style={{ display: 'grid', gap: 8, marginTop: 16 }}>
          <input placeholder="email" value={email} onChange={e => setEmail(e.target.value)} />
          <input placeholder="password" type="password" value={password} onChange={e => setPassword(e.target.value)} />
          <div style={{ display: 'flex', gap: 8 }}>
            <button onClick={register}>Register</button>
            <button onClick={login}>Login</button>
          </div>
          <small>After login, youâ€™ll see Link Bank and Transactions below.</small>
        </div>
      ) : (
        <>
          <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
            <LinkBankButton jwt={jwt} />
            <button onClick={() => setJwt('')}>Logout</button>
          </div>
          <Dashboard jwt={jwt} />
        </>
      )}
    </div>
  );
}
